example.code:
var dashCd = new Cooldown(3f);

void Update(float dt) {
    // TickAndCheckReady reduces time and returns true if ready
    if (dashCd.TickAndCheckReady(dt) && Input.Dash) {
        Dash();
        dashCd.Reset();
    }
}

var castTime = new Timer(2.0f);

void Update(float dt) {
    // TickAndCheckComplete returns true when Current >= Duration
    if (castTime.TickAndCheckComplete(dt)) {
        FinishCast();
        castTime.Reset();
    }
}
